INSERT INTO `problem` VALUES ('1000', '汽水瓶', '<p>\r\n	<!--[if gte mso 9]>Normal07.8 磅02falsefalsefalseMicrosoftInternetExplorer4<![endif]--><!--[if gte mso 9]><![endif]--><!--[if gte mso 10]>\r\n<![endif]-->\r\n</p>\r\n<p style=\"text-indent:20.0pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;\">有这样一道智力题：“</span><span style=\"font-family:宋体;\">某商店</span><span style=\"font-family:宋体;\">规定：</span><span style=\"font-family:宋体;\">三个空汽水瓶可以换一瓶汽水</span><span style=\"font-family:宋体;\">。</span><span style=\"font-family:宋体;\">小张手上有十个空汽水瓶，她最多可以换多少瓶汽水喝？</span><span style=\"font-family:宋体;\">”</span><span style=\"font-family:宋体;\">答案是</span><span>5</span><span style=\"font-family:宋体;\">瓶</span><span style=\"font-family:宋体;\">，方法如下</span><span style=\"font-family:宋体;\">：先用</span><span>9</span><span style=\"font-family:宋体;\">个空瓶子换</span><span>3</span><span style=\"font-family:宋体;\">瓶汽水，喝掉</span><span>3</span><span style=\"font-family:宋体;\">瓶满的，喝完以后</span><span>4</span><span style=\"font-family:宋体;\">个空瓶子，用</span><span>3</span><span style=\"font-family:宋体;\">个再换一瓶，喝掉这瓶满的，这时候剩</span><span>2</span><span style=\"font-family:宋体;\">个空瓶子。然后你让老板先借给你一瓶汽水，喝掉这瓶满的，喝完以后用</span><span>3</span><span style=\"font-family:宋体;\">个空瓶子换一瓶满的还给老板。如果小张手上有</span><i><span>n</span></i><span style=\"font-family:宋体;\">个空汽水瓶，最多可以换多少瓶汽水喝？</span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span>&nbsp;</span>\r\n</p>\r\n<h1>\r\n	<span style=\"font-size:12.0pt;font-family:黑体;\">输入</span>\r\n</h1>\r\n<p style=\"text-align:justify;text-indent:20.0pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;\">输入文件最多包含</span><span>10</span><span style=\"font-family:宋体;\">组测试数据，每个数据占一行，仅包含一个正整数</span><i><span>n</span></i><span style=\"font-family:宋体;\">（</span><span>1&lt;=<i>n</i>&lt;=100</span><span style=\"font-family:宋体;\">），表示小张手上的空汽水瓶数。</span><i><span>n</span></i><span>=0</span><span style=\"font-family:宋体;\">表示输入结束，你的程序不应当处理这一行。</span>\r\n</p>\r\n<p class=\"MsoFooter\">\r\n	<span style=\"font-family:&quot;\">&nbsp;</span>\r\n</p>\r\n<h1>\r\n	<span style=\"font-size:12.0pt;font-family:黑体;\">输出</span>\r\n</h1>\r\n<p style=\"text-align:justify;text-indent:20.0pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;\">对于每组测试数据，输出一行，表示最多可以喝的汽水瓶数。如果一瓶也</span><span style=\"font-family:宋体;\">喝不到</span><span style=\"font-family:宋体;\">，输出</span><span>0</span><span style=\"font-family:宋体;\">。</span>\r\n</p>', '', '', '3\r\n10\r\n81\r\n0\r\n', '1\r\n5\r\n40\r\n', '1', '', '湖南省第六届大学生计算机程序设计竞赛', '2016-05-23 13:13:46', '1', '128', 'N', '1', '11', '0');
INSERT INTO `problem` VALUES ('1001', '弟弟的作业', '<p><span lang=\"EN-US\" style=\"font-size:10.0pt;font-family:\n宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-bidi-font-family:&quot;Times New Roman&quot;;\nmso-ansi-language:EN-US;mso-fareast-language:EN-US;mso-bidi-language:AR-SA\">你的弟弟刚做完了</span><span lang=\"EN-US\" style=\"font-size:10.0pt;font-family:&quot;Times New Roman&quot;;mso-fareast-font-family:\n&quot;Times New Roman&quot;;mso-ansi-language:EN-US;mso-fareast-language:EN-US;\nmso-bidi-language:AR-SA\">&ldquo;100</span><span lang=\"EN-US\" style=\"font-size:10.0pt;\nfont-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-bidi-font-family:\n&quot;Times New Roman&quot;;mso-ansi-language:EN-US;mso-fareast-language:EN-US;\nmso-bidi-language:AR-SA\">以内数的加减法</span><span lang=\"EN-US\" style=\"font-size:10.0pt;\nfont-family:&quot;Times New Roman&quot;;mso-fareast-font-family:&quot;Times New Roman&quot;;\nmso-ansi-language:EN-US;mso-fareast-language:EN-US;mso-bidi-language:AR-SA\">&rdquo;</span><span lang=\"EN-US\" style=\"font-size:10.0pt;font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;\nmso-bidi-font-family:&quot;Times New Roman&quot;;mso-ansi-language:EN-US;mso-fareast-language:\nEN-US;mso-bidi-language:AR-SA\">这部分的作业，请你帮他检查一下。每道题目（包括弟弟的答案）的格式</span><span style=\"font-size:10.0pt;font-family:宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;\nmso-bidi-font-family:&quot;Times New Roman&quot;;mso-ansi-language:EN-US;mso-fareast-language:\nZH-CN;mso-bidi-language:AR-SA\">为</span><i><span lang=\"EN-US\" style=\"font-size:10.0pt;font-family:&quot;Times New Roman&quot;;mso-fareast-font-family:\n宋体;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA\">a</span></i><span lang=\"EN-US\" style=\"font-size:10.0pt;font-family:&quot;Times New Roman&quot;;mso-fareast-font-family:\n宋体;mso-ansi-language:EN-US;mso-fareast-language:ZH-CN;mso-bidi-language:AR-SA\">+<i>b</i>=<i>c</i></span><span style=\"font-size:10.0p