INSERT INTO `problem` VALUES ('1037', '【C语言训练】求s=a+aa+aaa+aaaa+aa...a的值', '<p><font size=\"3\" face=\"Times New Roman\">求s=a+aa+aaa+aaaa+aa...a的值，其中a是一个一位的整数。  <br />\n例如2+22+222+2222+22222(此时共有5个数相加)</font></p>', '<p><font size=\"3\" face=\"Times New Roman\">整数a和n（n个数相加，1&lt;= n, a&lt;=9）</font></p>', '<p><font size=\"3\" face=\"Times New Roman\">s的值</font></p>', '2 2', '24', '0', '', '', '2016-05-17 20:28:59', '1', '128', 'N', '0', '0', '0');
INSERT INTO `problem` VALUES ('1038', '【C语言训练】求具有abcd=(ab+cd)2性质的四位数', '<p><font size=\"3\" face=\"Times New Roman\">3025这个数具有一种独特的性质：将它平分为二段，即30和25，使之相加后求平方，即(30+25)2，恰好等于3025本身。请求出具有这样性质的全部四位数</font></p>', '', '<p><font face=\"Times New Roman\">满足题意的数全部四位数(从小到大输出,且数之间用两个空格分开) </font><br />\n<br />\n</p>', '', '2025  3025  9801  ', '0', '<p><font size=\"3\" face=\"Times New Roman\">根据题意可以采用穷举法，对所有四位数进行判断，从而筛选出符合这种性质的四位数。具体算法实现，可任取一个四位数，将其截为两部分，前两位为a，后两位为b，然后套用公式计算并判断。</font></p>', '', '2016-05-17 20:28:59', '1', '128', 'N', '0', '0', '0');
INSERT INTO `problem` VALUES ('1039', '【C语言训练】求函数值', '<p><font size=\"3\" face=\"Times New Roman\">按如下递归公式求函数值。  <br />\nx=1时 f(x)=10；x&gt;1时 f(x)=f(x-1)+2</font></p>', '<p><font size=\"3\" face=\"Times New Roman\">整型变量x</font></p>', '<p><font size=\"3\" face=\"Times New Roman\">f(x)</font></p>', '10', '28', '0', '', '', '2016-05-17 20:28:59', '1', '128', 'N', '0', '0', '0');
