INSERT INTO `source_code` VALUES ('1018', '#include<iostream>\n#include<stack>\n#include<string>\nusing namespace std;\n\nint main()\n{\n	int n=0,m;\n	string s;\n	stack<string> S0,S1,S2;\n	while(cin>>m>>s)\n	{\n		n++;\n		if(!m)\n			S0.push(s);\n		else if(m==1)\n			S1.push(s);\n		else\n			S2.push(s);\n	}\n	for(m=0;m<10;m++)\n	{\n		if(S0.empty())\n			cout<<0<<\' \';\n		else\n		{\n			cout<<S0.top()<<\' \';\n			S0.pop();\n		}\n		if(S1.empty())\n			cout<<0<<\' \';\n		else\n		{\n			cout<<S1.top()<<\' \';\n			S1.pop();\n		}\n		if(S2.empty())\n			cout<<0<<endl;\n		else\n		{\n			cout<<S2.top()<<endl;\n			S2.pop();\n		}\n	}\n	while(!S0.empty())\n	{\n		cout<<S0.top()<<\' \';\n		S0.pop();\n	}\n	while(!S1.empty())\n	{\n		cout<<S1.top()<<\' \';\n		S1.pop();\n	}\n	while(!S2.empty())\n	{\n		cout<<S2.top()<<endl;\n		S2.pop();\n	}\n	return 0;\n}');
INSERT INTO `source_code` VALUES ('1019', '#include<stdio.h>\n#include<string.h>\n\nchar r[1000];\n\nvoid bc(char n[],int s,int e)\n{\n	int i,flag,k,j,a[1000],b[1000];\n	for(i=0;i<strlen(n);i++)\n		a[i]=n[i]-48;\n	k=0;\n	while(1)\n	{\n		for(i=0;i<strlen(n);i++)\n		{\n			if(a[i]!=0&&i<strlen(n)-1)\n			{\n				a[i+1]+=(a[i]%e)*s;\n				a[i]/=e;\n			}\n			if(i==strlen(n)-1)\n			{\n				b[k++]=a[i]%e;\n				a[i]/=e;\n			}\n		}\n		flag=0;\n		for(i=0;i<strlen(n);i++)\n			if(a[i]!=0)\n				flag=1;\n		if(!flag)\n			break;\n	}\n	for(j=0,i=k-1;i>=0;i--)\n		r[j++]=b[i]+48;\n	r[j]=\'\\0\';\n}\n\nint main()\n{\n	char n[1000];\n	while(scanf(\"%s\",n)!=EOF)\n	{\n		bc(n,10,2);\n		puts(r);\n	}\n	return 0;\n}');
