INSERT INTO `source_code_user` VALUES ('1002', '#include<stdio.h>\nint main() {\n  char s[99];\n  int a, b, c, n = 0;\n  while(scanf(\"%s\", &s) == 1) {\n    if(sscanf(s, \"%d+%d=%d\", &a, &b, &c) == 3 && a+b==c) n++;\n    if(sscanf(s, \"%d-%d=%d\", &a, &b, &c) == 3 && a-b==c) n++;\n  }\n  printf(\"%d\\n\", n);\n  return 0;\n}\n');
