INSERT INTO `source_code_user` VALUES ('1024', '#include<cstdio>\n#include<algorithm>\nusing namespace std;\n\nstruct P\n{\n	int c,in;\n}a[100],b[100];\n\nbool cmp(P a,P b)\n{\n	return a.in>b.in;\n}\n\nint main()\n{\n	int m,n,i,j;\n	scanf(\"%d%d\",&m,&n);\n	for(i=0;i<m;i++)\n		scanf(\"%d%d\",&a[i].c,&a[i].in);\n	for(i=0;i<n;i++)\n		scanf(\"%d%d\",&b[i].c,&b[i].in);\n	sort(a,a+m,cmp);\n	sort(b,b+n,cmp);\n	i=j=0;\n	while(i<m||j<n)\n	{\n		if(a[i].in==b[j].in)\n		{\n			if(a[i].c+b[j].c)\n				printf(\"%d %d\\n\",a[i].c+b[j].c,a[i].in);\n			i++;\n			j++;\n		}\n		else if(a[i].in<b[j].in)\n		{\n			if(b[j].c)\n				printf(\"%d %d\\n\",b[j].c,b[j].in);\n			j++;\n		}\n		else\n		{\n			if(a[i].c)\n				printf(\"%d %d\\n\",a[i].c,a[i].in);\n			i++;\n		}\n	}\n	return 0;\n} ');
